//Additions functions
function go(screen) {
    document.querySelectorAll(".screen").forEach(s => s.style.display = "none");
    document.getElementById("screen-" + screen).style.display = "block";
}

function hasStoredWallet() {
  const raw = localStorage.getItem("forgex_wallet");
  return raw !== null;
}

function initApp() {
  if (hasStoredWallet()) {
    go("unlock");
  } else {
    go("welcome");
  }
}

function save_data(address, publicKey, encryptedPrivateKey) {
  const wallet = {
    address: address,
    public_key: publicKey,
    private_key: encryptedPrivateKey,
  };

  localStorage.setItem("forgex_wallet", JSON.stringify(wallet));
}

function load_data() {
  const raw = localStorage.getItem("forgex_wallet");
  if (!raw) {
    return null;
  }

  try {
    return JSON.parse(raw);
  } catch (e) {
    console.error("Error reading wallet:", e);
    return null;
  }
}

function delete_data() {
  localStorage.removeItem("forgex_wallet");
}

function from_utf8(str) {
  return new TextEncoder().encode(str);
}

function to_utf8(bytes) {
  return new TextDecoder().decode(bytes);
}

function normalize_bytes(b) {
  if (b instanceof Uint8Array) return b;
  if (Array.isArray(b)) return new Uint8Array(b);
  if (typeof b === "object" && b !== null) {
    return new Uint8Array(Object.values(b));
  }
  throw new Error("Unsupported byte format");
}

function to_hex(byteArray) {
  const bytes = normalize_bytes(byteArray);
  let hexString = '';
  for (let i = 0; i < bytes.length; i++) {
    hexString += bytes[i].toString(16).padStart(2, '0');
  }
  return hexString;
}

function from_hex(hex) {
    const out = new Uint8Array(hex.length / 2);
    for (let i = 0; i < out.length; i++)
        out[i] = parseInt(hex.substr(i*2,2), 16);
    return out;
}

function combine_bits(bytes, hash) {
    let bits128 = "";
    for (let i = 0; i < bytes.length; i++) {
        bits128 += bytes[i].toString(2).padStart(8, "0");
    }
    const firstByte = hash[0];
    const first4 = (firstByte >> 4).toString(2).padStart(4, "0");
    return bits128 + first4;
}

function bits_to_words(bits) {
    const words = [];

    for (let i = 0; i < 12; i++) {
        const chunk = bits.slice(i * 11, i * 11 + 11);
        const index = parseInt(chunk, 2);
        words.push(WORDLIST[index]);
    }

    return words;
}

function pbkdf2(words) {
    const mnemonic = words.join(" ");
    const salt = "forgex";
    bytes = from_hex(CryptoJS.PBKDF2(mnemonic, salt, { keySize: 64/4, iterations: 2048, hasher: CryptoJS.algo.SHA512 }).toString());
    return bytes;
}

function priv_key(seed) {
    return seed.slice(0, 32);
}

function pub_key(priv_key) {
    const EC = elliptic.ec;
    const ec = new EC("secp256k1");

    const privHex = to_hex(priv_key);

    const key = ec.keyFromPrivate(privHex);

    const pubHex = key.getPublic(true, "hex");
    const out = from_hex(pubHex);

    return out;
}


function bech32Polymod(values) {
    let chk = 1;
    const GEN = [0x3b6a57b2,0x26508e6d,0x1ea119fa,0x3d4233dd,0x2a1462b3];
    for (let v of values) {
        const top = chk >>> 25;
        chk = ((chk & 0x1ffffff) << 5) ^ v;
        for (let i = 0; i < 5; i++) {
            if ((top >>> i) & 1) chk ^= GEN[i];
        }
    }
    return chk;
}

function bech32HrpExpand(hrp) {
    const ret = [];
    for (let i = 0; i < hrp.length; i++) ret.push(hrp.charCodeAt(i) >> 5);
    ret.push(0);
    for (let i = 0; i < hrp.length; i++) ret.push(hrp.charCodeAt(i) & 31);
    return ret;
}

function bech32CreateChecksum(hrp, data) {
    const values = bech32HrpExpand(hrp).concat(data).concat([0,0,0,0,0,0]);
    const mod = bech32Polymod(values) ^ 1;
    const ret = [];
    for (let p = 0; p < 6; p++) {
        ret.push((mod >> (5 * (5 - p))) & 31);
    }
    return ret;
}

function bech32Encode(hrp, data) {
    const checksum = bech32CreateChecksum(hrp, data);
    const combined = data.concat(checksum);
    let out = hrp + "1";
    for (let v of combined) out += BECH32_CHARSET[v];
    return out;
}

function bech32Decode(addr) {
  if (typeof addr !== "string") {
    throw new Error("Address must be string");
  }

  addr = addr.toLowerCase();

  const pos = addr.lastIndexOf("1");
  if (pos < 1 || pos + 7 > addr.length) {
    throw new Error("Invalid bech32 format");
  }

  const hrp = addr.slice(0, pos);
  const data = [];

  for (let i = pos + 1; i < addr.length; i++) {
    const c = addr[i];
    const v = BECH32_CHARSET.indexOf(c);
    if (v === -1) {
      throw new Error("Invalid character in bech32 string");
    }
    data.push(v);
  }

  return { hrp, data };
}

function convertBits(data, fromBits, toBits, pad = true) {
    let acc = 0;
    let bits = 0;
    const maxv = (1 << toBits) - 1;
    const ret = [];
    for (let value of data) {
        if (value < 0 || (value >> fromBits) !== 0) return null;
        acc = (acc << fromBits) | value;
        bits += fromBits;
        while (bits >= toBits) {
            bits -= toBits;
            ret.push((acc >> bits) & maxv);
        }
    }
    if (pad) {
        if (bits > 0) {
            ret.push((acc << (toBits - bits)) & maxv);
        }
    } else if (bits >= fromBits || ((acc << (toBits - bits)) & maxv)) {
        return null;
    }
    return ret;
}

function address(pubKey) {
    if (!(pubKey instanceof Uint8Array) || pubKey.length !== 33) {
        throw new Error("pubKey must be Uint8Array(33)");
    }

    const core = pubKey.slice(1);

    const payload24 = new Uint8Array(24);
    payload24[0] = "g".charCodeAt(0);
    payload24[1] = "l".charCodeAt(0);
    payload24[2] = "d".charCodeAt(0);
    payload24[3] = "2".charCodeAt(0);
    payload24.set(core.slice(0, 20), 4);

    const hash = sha256.array(payload24);

    const full28 = new Uint8Array(28);
    full28.set(payload24, 0);
    full28.set(hash.slice(0, 4), 24);

    const data5 = convertBits(full28, 8, 5, true);

    const addr = bech32Encode("gld2", data5);

    return addr;
}

function is_valid_address(addr) {
  try {
    if (typeof addr !== "string") return false;

    const decoded = bech32Decode(addr);
    if (!decoded || decoded.hrp !== "gld2") return false;

    const data5 = decoded.data;
    const fullBytes = Uint8Array.from(convertBits(data5, 5, 8, true));
    if (fullBytes.length < 28) return false;

    const full28 = fullBytes.slice(0, 28);

    if (
      full28[0] !== "g".charCodeAt(0) ||
      full28[1] !== "l".charCodeAt(0) ||
      full28[2] !== "d".charCodeAt(0) ||
      full28[3] !== "2".charCodeAt(0)
    ) {
      return false;
    }

    const payload24 = full28.slice(0, 24);
    const checksum = full28.slice(24, 28);
    const hash = sha256.array(payload24);

    for (let i = 0; i < 4; i++) {
      if (checksum[i] !== hash[i]) return false;
    }

    return true;
  } catch (e) {
    return false;
  }
}


function create_new_words() {
    const bytes = new Uint8Array(16);
    window.crypto.getRandomValues(bytes);
    var bytes_hash = sha256.array(bytes);
    var combined_bytes = combine_bits(bytes, bytes_hash);
    var words = bits_to_words(combined_bytes);
    create_keys_from_words(words);
    for (let i = 0; i < 12; i++) {
        create_table[i].innerText = words[i];
    }
    return words;
}

function create_keys_from_words(words) {
    const seed = pbkdf2(words);
    var private_key = priv_key(seed);
    var public_key = pub_key(private_key);
    var addr = address(public_key);
    priv_key = private_key;
    pub_key = public_key;
    address = addr;
}

async function encrypt_key(privkey, password) {
  const enc = new TextEncoder();

  const keyHash = await crypto.subtle.digest("SHA-256", enc.encode(password));
  const aesKey = await crypto.subtle.importKey("raw", keyHash, "AES-GCM", false, ["encrypt"]);

  const iv = crypto.getRandomValues(new Uint8Array(12));

  const encrypted = await crypto.subtle.encrypt({ name: "AES-GCM", iv }, aesKey, privkey);

  return {
    iv: btoa(String.fromCharCode(...iv)),
    data: btoa(String.fromCharCode(...new Uint8Array(encrypted)))
  };
}

function verify_password(p1, p2) {
  if (!p1 || !p2) return "Password cannot be empty";
  if (p1.length < 6) return "Password must be at least 6 characters";
  if (p1.includes(" ")) return "Password cannot contain spaces";
  if (p1 !== p2) return "Passwords do not match";
  return "ok";
}

async function decrypt_key(encrypted, password) {
  try {
    const enc = new TextEncoder();

    const keyHash = await crypto.subtle.digest("SHA-256", enc.encode(password));
    const aesKey = await crypto.subtle.importKey("raw", keyHash, "AES-GCM", false, ["decrypt"]);

    const iv = Uint8Array.from(atob(encrypted.iv), c => c.charCodeAt(0));
    const data = Uint8Array.from(atob(encrypted.data), c => c.charCodeAt(0));

    const decrypted = await crypto.subtle.decrypt(
      { name: "AES-GCM", iv: iv },
      aesKey,
      data
    );

    return {
      ok: true,
      privateKey: new Uint8Array(decrypted),
    };

  } catch (e) {
    return {
      ok: false,
      privateKey: null,
    };
  }
}


//Dashboard functions

function load_dashboard() {
    go("dashboard");
    dashboard_address.innerText = address;
    dashboard_balance.innerText = load_balance() + "GLD";
}

function calculate_fee(amount) {
    return amount * 0.01;
}

//Conections functions

function Get(command) {
    const urla = url + command;
    const xhr = new XMLHttpRequest();
    xhr.open("GET", urla, false);
    xhr.send(null);
    return xhr.responseText;
}

function Post(command, data) {
    const urla = url + command;
    const xhr = new XMLHttpRequest();
    xhr.open("POST", urla, false);
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.send(data);
    return xhr.responseText;
}

function load_balance() {
    var response = JSON.parse(Get("/balance?address=" + address));
    return response.balance / 1000000;
}

function load_nonce() {
  var response = JSON.parse(Get("/nonce?address=" + address));
  return response.nonce;
}

function send_tx(tx_json) {
    var response = JSON.parse(Post("/broadcast_tx", tx_json));
    console.log("Response: " + response.status);
    console.log("Tx_hash: " + response.tx_hash);
}

//Signature functions

function pipe_v1_merge(...parts) {
  return parts
    .map(s => s.replace(/\s+/g, ""))
    .join("");
}

function sign_tx(message) {
  if (!(priv_key instanceof Uint8Array) || priv_key.length !== 32) {
    throw new Error("priv_key must be Uint8Array(32)");
  }

  const msgBytes = from_utf8(message);

  const hashBytes = sha256.array(msgBytes);
  const hashHex = to_hex(hashBytes);

  const privHex = to_hex(priv_key);

  const key = ec.keyFromPrivate(privHex);
  const sig = key.sign(hashHex, { canonical: true });

  const rBytes = sig.r.toArrayLike(Uint8Array, "be", 32);
  const sBytes = sig.s.toArrayLike(Uint8Array, "be", 32);

  const v = sig.recoveryParam;
  if (v === null || v === undefined) {
    throw new Error("No recoveryParam from signature");
  }

  const sigBytes = new Uint8Array(65);
  sigBytes.set(rBytes, 0);
  sigBytes.set(sBytes, 32);
  sigBytes[64] = v;

  return sigBytes;
}

function build_tx_json(domain_tag, chain_id, tx_type, to, token, amount, fee, nonce, timestamp_ms, sigBytes, encoding) {
  if (!address || !pub_key || !priv_key) {
    throw new Error("Wallet is not unlocked");
  }
  const from = address;
  const sigHex = to_hex(sigBytes);
  const pubHex = to_hex(pub_key);

  const tx = {
    domain_tag: domain_tag,
    chain_id: chain_id,
    tx_type: tx_type,
    from: from,
    to: to,
    token: token,
    amount: amount,
    fee: fee,
    nonce: nonce,
    timestamp_ms: timestamp_ms,
    pub_key: pubHex,
    sig: sigHex,
    encoding: encoding
  };

  return JSON.stringify(tx);
}

